import React, { useState } from 'react';
import './App.css';

function PasswordUpdateForm() {
  const [current, setCurrent] = useState('');
  const [newPass, setNewPass] = useState('');
  const [visible, setVisible] = useState(false);

  const toggleVisibility = () => setVisible(!visible);

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Mot de passe modifié (démo)');
  };

  return (
    <form className="password-form" onSubmit={handleSubmit}>
      <h3>Modifier le mot de passe</h3>

      <div className="form-group">
        <label>Mot de passe actuel</label>
        <input
          type={visible ? 'text' : 'password'}
          value={current}
          onChange={(e) => setCurrent(e.target.value)}
        />
      </div>

      <div className="form-group">
        <label>Nouveau mot de passe</label>
        <div className="password-input">
          <input
            type={visible ? 'text' : 'password'}
            value={newPass}
            onChange={(e) => setNewPass(e.target.value)}
          />
          <span onClick={toggleVisibility} className="toggle-eye">
          </span>
        </div>
      </div>

      <button type="submit">Valider</button>
    </form>
  );
}

const App = () => {
  const locataire = {
    id: 12,
    nom: 'Rahman',
    prenom: 'Sami',
    email: 'samirahman002@gmail.com',
    telephone: '+41 856 569 909',
    inscription: '2023-11-12',
    photo: 'https://via.placeholder.com/100',
    compteActif: '8040 5630 8802 4525',
    factures: [
      { type: 'Téléphone', status: 'payée' },
      { type: 'Loyer', status: 'impayée' },
      { type: 'Taxe', status: 'payée' }
    ]
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="logo">GSI</div>
        <ul>
          <li className="active">Mon profil</li>
          <li>Mes locations</li>
          <li>Documents</li>
          <li>Factures</li>
          <li>Support</li>
        </ul>
      </aside>

      <main className="content">
        <header className="topbar">
          <p>Bonjour {locataire.prenom} {locataire.nom}</p>
          <img src={locataire.photo} alt="profil" className="avatar" />
        </header>

        <div className="profil-container">


        <section className="profile-card styled">
  <img src={locataire.photo} alt="Photo de profil" className="profile-avatar" />

  <div className="profile-label">My profile</div>

  <div className="profile-line">
    <span><strong>ID :</strong></span>
    <span>{locataire.id}</span>
  </div>
  <div className="profile-line">
    <span><strong>Nom :</strong></span>
    <span>{locataire.nom.toUpperCase()}</span>
  </div>
  <div className="profile-line">
    <span><strong>Prénom :</strong></span>
    <span>{locataire.prenom}</span>
  </div>
  <div className="profile-line">
    <span><strong>Téléphone :</strong></span>
    <span>{locataire.telephone}</span>
  </div>
  <div className="profile-line">
    <span><strong>Email :</strong></span>
    <span>{locataire.email}</span>
  </div>
  <div className="profile-line">
    <span><strong>Inscrit le :</strong></span>
    <span>{new Date(locataire.inscription).toLocaleDateString('fr-FR')}</span>
  </div>

  <div className="profile-sms">
    <span>SMS alerts activation</span>
    <span className="dot green"></span>
  </div>

  <button className="save-btn">Save</button>
</section>



  <PasswordUpdateForm />
</div>



      </main>
    </div>
  );
};

export default App;
