import React, { useState } from 'react';
import './App.css';

function PasswordUpdateForm() {
  const [current, setCurrent] = useState('');
  const [newPass, setNewPass] = useState('');
  const [visible, setVisible] = useState(false);

  const toggleVisibility = () => setVisible(!visible);

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Mot de passe modifi√© (d√©mo)');
  };

  return (
    <form className="password-form" onSubmit={handleSubmit}>
      <h3>Modifier le mot de passe</h3>

      <div className="form-group">
        <label>Mot de passe actuel</label>
        <input
          type={visible ? 'text' : 'password'}
          value={current}
          onChange={(e) => setCurrent(e.target.value)}
        />
      </div>

      <div className="form-group">
        <label>Nouveau mot de passe</label>
        <div className="password-input">
          <input
            type={visible ? 'text' : 'password'}
            value={newPass}
            onChange={(e) => setNewPass(e.target.value)}
          />
          <span onClick={toggleVisibility} className="toggle-eye">
            {visible ? 'üôà' : 'üëÅÔ∏è'}
          </span>
        </div>
      </div>

      <button type="submit">Valider</button>
    </form>
  );
}

const App = () => {
  const locataire = {
    nom: 'Rahman',
    prenom: 'Sami',
    email: 'samirahman002@gmail.com',
    telephone: '+41 856 569 909',
    photo: 'https://via.placeholder.com/100',
    compteActif: '8040 5630 8802 4525',
    factures: [
      { type: 'T√©l√©phone', status: 'pay√©e' },
      { type: 'Loyer', status: 'impay√©e' },
      { type: 'Taxe', status: 'pay√©e' }
    ]
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="logo">GSI</div>
        <ul>
          <li className="active">Mon profil</li>
          <li>Mes locations</li>
          <li>Documents</li>
          <li>Factures</li>
          <li>Support</li>
        </ul>
      </aside>

      <main className="content">
        <header className="topbar">
          <p>Bonjour {locataire.prenom} {locataire.nom}</p>
          <img src={locataire.photo} alt="profil" className="avatar" />
        </header>

        <section className="profile-card improved">
          <img src={locataire.photo} alt="Photo de profil" className="profile-pic"/>
          <div className="profile-info">
            <h2>{locataire.prenom} {locataire.nom}</h2>
            <p>{locataire.telephone}</p>
            <p>{locataire.email}</p>
            <button>Modifier le profil</button>
          </div>
        </section>

        <PasswordUpdateForm />

        <section className="cards">
          <div className="card">
            <h3>Mes comptes</h3>
            <p>Compte actif : {locataire.compteActif}</p>
            <button className="danger">Bloquer</button>
          </div>
          <div className="card">
            <h3>Mes factures</h3>
            <ul>
              {locataire.factures.map((facture, i) => (
                <li key={i}>
                  {facture.type} : <span className={facture.status === 'pay√©e' ? 'paid' : 'unpaid'}>
                    {facture.status}
                  </span>
                </li>
              ))}
            </ul>
            <button className="small-button">Voir derni√®re facture</button>
          </div>
        </section>
      </main>
    </div>
  );
};

export default App;
