import React, { useState } from 'react';
import './App.css';

function PasswordUpdateForm() {
  const [current, setCurrent] = useState('');
  const [newPass, setNewPass] = useState('');
  const [visible, setVisible] = useState(false);

  const toggleVisibility = () => setVisible(!visible);

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Mot de passe modifié (démo)');
  };

  return (
    <section className="password-form">
      <h3>Modifier le mot de passe</h3>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Mot de passe actuel</label>
          <input
            type={visible ? 'text' : 'password'}
            value={current}
            onChange={(e) => setCurrent(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Nouveau mot de passe</label>
          <div className="password-input">
            <input
              type={visible ? 'text' : 'password'}
              value={newPass}
              onChange={(e) => setNewPass(e.target.value)}
            />
            <span onClick={toggleVisibility} className="toggle-eye">
              {visible ? '🙈' : '👁️'}
            </span>
          </div>
        </div>

        <button type="submit" className="blue-btn">Valider</button>
      </form>
    </section>
  );
}

const App = () => {
  const locataire = {
    id: 12,
    nom: 'Rahman',
    prenom: 'Sami',
    email: 'samirahman002@gmail.com',
    telephone: '+41 856 569 909',
    inscription: '2023-11-12',
    photo: 'https://via.placeholder.com/100',
    compteActif: '8040 5630 8802 4525',
    factures: [
      { type: 'Téléphone', status: 'payée' },
      { type: 'Loyer', status: 'impayée' },
      { type: 'Taxe', status: 'payée' }
    ]
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="logo">GSI</div>
        <ul>
          <li className="active">Mon profil</li>
          <li>Mes locations</li>
          <li>Documents</li>
          <li>Factures</li>
          <li>Support</li>
        </ul>
      </aside>

      <main className="content">
        <header className="topbar">
          <p>Bonjour {locataire.prenom} {locataire.nom}</p>
          <img src={locataire.photo} alt="profil" className="avatar" />
        </header>

        <div className="profil-container">
  {/* Profil */}
  <section className="profile-card styled">
    {/* ...infos du locataire... */}
    <button className="save-btn">Modifier</button>
  </section>

  {/* Colonne droite : 2 cartes empilées */}
  <div className="column-right">
    <section className="card password-card">
      <h3>Modifier le mot de passe</h3>
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="form-group">
          <label>Mot de passe actuel</label>
          <input type="password" />
        </div>
        <div className="form-group">
          <label>Nouveau mot de passe</label>
          <input type="password" />
        </div>
        <button className="blue-btn">Valider</button>
      </form>
    </section>

    <section className="card facture-card">
      <h3>Mes factures</h3>
      <button className="small-button">Voir la dernière facture</button>
    </section>
  </div>
</div>

      </main>
    </div>
  );
};

export default App;
